//sortable 비활성화 
scwin.trigger2_onclick = function(e) {
    $( "#mf_grp_drag" ).sortable("disable");    //sortable 비활성화 (사전에 미리 sortable 지정 한 후 활성화 여부 셋팅) 
    $( "#mf_grp_drag" ).enableSelection();     //text selection 방지해제  
};

scwin.trigger3_onclick = function(e) {
